import { useState, useEffect } from "react";

const useFetch = (url: string) => {
  const [data, setData] = useState(null);

  useEffect(() => {
    if(data) {
    try {
        fetch(url)
        .then((res) => res.json())
        .then((data) => setData(data));
    } catch(error){
        console.log(error);
    }
    }
  }, [data]);

  return [data];
};

export default useFetch;
