import React from "react";
import './Feed.css';
import { ButtonData, HeaderData, TweetData, UserData } from "../../types/Models";
import ProfilePicture from "../ProfilePicture/ProfilePicture";
import { TweetButton } from "../Navbar/Navbar";

interface FeedProps {
    headerData: HeaderData;
    userData: UserData;
    tweetThreads: TweetData[][];
    tweetButton: ButtonData;
}

interface HeaderProps {
    headerData: HeaderData;
}

interface TweetListProps {
    tweetListData: TweetData[][];
}

interface TweetProps {
    tweetData: TweetData[];
}

interface TweetPostProps {
    userData: UserData;
    tweetButton: ButtonData;
}

interface SingleTweetProps {
    singleTweet: TweetData;
    isFirst?: boolean;
    isLast?: boolean;
}

const Feed: React.FC<FeedProps> = ({headerData, tweetThreads, userData, tweetButton}) => {
    const singleTweet = {
        "user": {
            "userName": "Kartikey Sharma",
            "userId": "Kartikey_1809",
            "imageData": {
                "url": "https://assets.nextleap.app/img/9708F944-928E-4DF0-BE77-9924B13CFA85-88d2f244-adc4-4428-9c79-7f9bd23363f8.JPG",
                "alt": "kartikeya"
            },
            "blueTick": true,
            "joiningDate": 1582140603000,
            "following": 570,
            "followers": 160,
            "desc": "Finding Patterns in Chaos"
        },
        "tweetTime": 1678649733000,
        "textArea": "It's the year 2021 and we still don't have flying cars.",
        "replies": 10400,
        "reTweets": 1100,
        "views": 107000,
        "likes": 506000,
        "id": "97t943x7",
        "threadId": "rvujohdf"
    };
    return(
        <div className="feed">
            <Header headerData={headerData}/>
            <TweetPost userData={userData} tweetButton={tweetButton}/>
            <Tweet tweetData={tweetThreads[0]} />
            <SingleTweet singleTweet={singleTweet} />
        </div>
    )
}

const Header: React.FC<HeaderProps> = ({headerData}) => {
    return (
        <div>
        <h3 className="home-text">{headerData.title.text}</h3>
        <div className="header-btns">
            <div className="for-you">
                <h5>{headerData.navigationTabs![0].buttonText}</h5>
            </div>
            <div className="following">
                <h5>{headerData.navigationTabs![1].buttonText}</h5>
            </div>
        </div>
        </div>
    )
}

const TweetList: React.FC<TweetListProps> = ({tweetListData}) => {
    return (
        <>
        </>
    )
}

const TweetPost: React.FC<TweetPostProps> = ({userData, tweetButton}) => {
    return (
        <div className="post">
        <div className="tweet-post">
            <ProfilePicture userData={userData} />
            <textarea placeholder="What's happening?">

            </textarea>
        </div>
        <TweetButton tweetButton={tweetButton} />
        </div>
    )
}

const Tweet: React.FC<TweetProps> = ({tweetData}) => {
    const sample = [
        {
            "user": {
                "userName": "Kartikey Sharma",
                "userId": "Kartikey_1809",
                "imageData": {
                    "url": "https://assets.nextleap.app/img/9708F944-928E-4DF0-BE77-9924B13CFA85-88d2f244-adc4-4428-9c79-7f9bd23363f8.JPG",
                    "alt": "kartikeya"
                },
                "blueTick": true,
                "joiningDate": 1582140603000,
                "following": 570,
                "followers": 160,
                "desc": "Finding Patterns in Chaos"
            },
            "tweetTime": 1678649733000,
            "textArea": "It's the year 2021 and we still don't have flying cars.",
            "replies": 10400,
            "reTweets": 1100,
            "views": 107000,
            "likes": 506000,
            "id": "97t943x7",
            "threadId": "rvujohdf"
        },
        {
            "user": {
                "userName": "Kartikey Sharma",
                "userId": "Kartikey_1809",
                "imageData": {
                    "url": "https://assets.nextleap.app/img/9708F944-928E-4DF0-BE77-9924B13CFA85-88d2f244-adc4-4428-9c79-7f9bd23363f8.JPG",
                    "alt": "kartikeya"
                },
                "blueTick": true,
                "joiningDate": 1582140603000,
                "following": 570,
                "followers": 160,
                "desc": "Finding Patterns in Chaos"
            },
            "tweetTime": 1678649733000,
            "textArea": "It's the year 2021 and we still don't have flying cars.",
            "replies": 10400,
            "reTweets": 1100,
            "views": 107000,
            "likes": 506000,
            "id": "9htv6p0a",
            "threadId": "h0iuiv47"
        }
    ]

    console.log(sample);
    return (
        <div className="threaded-tweet">
            <SingleTweet singleTweet={sample[0]} />
            <SingleTweet singleTweet={sample[0]} />
        </div>
    )
}

const formatTimestampToDate = (timestamp: number): string => {
    const date = new Date(timestamp);
    const options: Intl.DateTimeFormatOptions = { month: 'short', day: '2-digit' };
    return new Intl.DateTimeFormat('en-US', options).format(date);
}

const formatNumber = (value: number): string => {
    const suffixes = ["", "k", "M", "B", "T"]; // Add more suffixes as needed
    const suffixNum = Math.floor(("" + value).length / 3);

    let shortValue = parseFloat((suffixNum !== 0 ? value / Math.pow(1000, suffixNum) : value).toPrecision(3));
    if (shortValue % 1 !== 0) {
        shortValue = parseFloat(shortValue.toFixed(1));
    }

    return shortValue + suffixes[suffixNum];
}

const SingleTweet: React.FC<SingleTweetProps> = ({singleTweet, isFirst=true, isLast=false}) => {
    return (
        <div className="single-tweet">
            <div className="tweeter-info">
                <div className="tweeter-pic">
                <ProfilePicture userData={singleTweet.user}/>
                </div>
                <div className="tweet-body">
                <div className="tweeter-id">
                <p className="id-child">{singleTweet.user.userName}</p>
                {singleTweet.user.blueTick?(
                    <img className="id-child" width="15" height="15" src="https://img.icons8.com/color/48/instagram-verification-badge.png" alt="instagram-verification-badge"/>
                ) :
                (
                    <></>
                )}               
                <p className="id-child user-id">{singleTweet.user.userId}</p>
                <p className="id-child user-id">&#x2022;</p>
                <p className="id-child user-id">{formatTimestampToDate(singleTweet.tweetTime)}</p>
                </div>
                <div className="tweet-content id-child">
                    <p className="tweet-content id-child">{singleTweet.textArea}</p>
                </div>
                <div className="user-actions">
                    <div className="action-buttons">
                        <i className="fa-regular fa-comment action"></i>
                        <p className="id-child action">{formatNumber(singleTweet.replies)}</p>
                        <i className="fa-solid fa-retweet action"></i>
                        <p className="id-child action">{formatNumber(singleTweet.reTweets)}</p>
                        <i className="far fa-heart action"></i>
                        <p className="id-child action">{formatNumber(singleTweet.likes)}</p>
                        <i className="fa-solid fa-chart-simple action"></i>
                        <p className="id-child action">{formatNumber(singleTweet.views)}</p>
                    </div>
                </div>
                </div>
            </div>

        </div>
    )
}

export default Feed;
